<?php

namespace Colt\Query\Event;

use Colt\Query\InvariableQueryInterface;
use Colt\Query\Event\InvariableQueryEventInterface;
use Colt\Exception\Type\NonStringException;

/**
 * A representation of an event generated by an invariable query
 *
 * Implements an Immutable Object pattern
 *
 * @category   Colt
 * @package    Query
 * @subpackage Event
 * @version    $id$
 * @copyright  2013 Alex Butucea <alex826@gmail.com>
 * @author     Alex Butucea <alex826@gmail.com>
 * @license    The MIT License (MIT) {@link http://opensource.org/licenses/MIT}
 */
class InvariableQueryEvent extends AbstractQueryEvent implements
    InvariableQueryEventInterface
{
    /**
     * {@inheritdoc}
     */
    public function __construct(
        $topic,
        $content,
        InvariableQueryInterface $publisher
    ) {
        if (! is_string($topic)) {
            throw new NonStringException();
        }

        $this->topic = $topic;
        $this->content = $content;
        $this->publisher = $publisher;
    }
}
