<?php

namespace Deicer\Query\Event;

use Deicer\Query\InvariableQueryInterface;
use Deicer\Query\Event\QueryEventInterface;

/**
 * Interface for execution events generated by invariable queries
 *
 * @category   Deicer
 * @package    Query
 * @subpackage Event
 * @version    $id$
 * @copyright  2013 Alex Butucea <alex826@gmail.com>
 * @author     Alex Butucea <alex826@gmail.com>
 * @license    The MIT License (MIT) {@link http://opensource.org/licenses/MIT}
 */
interface InvariableQueryEventInterface extends
 QueryEventInterface
{
    /**
     * String serialization format
     * 
     * @const string
     */
    const SERIALIZED_FORMAT = 'Invariable Query Execution: %s | Result: "%s" | Elapsed Time: %dms | Content: %s';

    /**
     * Invariable query constructor
     *
     * @throws NonStringException If $topic is not a string
     * @param  string $topic See constants in QueryEventInterface
     * @param  mixed $content Content returned from the query's inner execution
     * @param  TokenizedQueryInterface $publisher Event originator
     * @return InvariableQueryEventInterface
     */
    public function __construct(
        $topic,
        $content,
        InvariableQueryInterface $publisher
    );
}
