<?php

namespace Deicer\Query\Event;

use Deicer\Query\ParameterizedQueryInterface;
use Deicer\Query\Event\QueryEventInterface;
use Deicer\Pubsub\EventInterface;
use Deicer\Stdlib\ParameterProviderInterface;

/**
 * Interface for execution events generated by parameterized queries
 *
 * @category   Deicer
 * @package    Query
 * @subpackage Event
 * @version    $id$
 * @copyright  2013 Alex Butucea <alex826@gmail.com>
 * @author     Alex Butucea <alex826@gmail.com>
 * @license    The MIT License (MIT) {@link http://opensource.org/licenses/MIT}
 */
interface ParameterizedQueryEventInterface extends
 ParameterProviderInterface,
 QueryEventInterface
{
    /**
     * String serialization format
     * 
     * @const string
     */
    const SERIALIZED_FORMAT = 'Parameterized Query Execution: %s | Result: "%s" | Elapsed Time: %dms | Params: %s | Content: %s';

    /**
     * Parameterized query constructor
     *
     * @throws NonStringException If $topic is not a string
     * @param  string $topic See constants in QueryEventInterface
     * @param  mixed $content Content returned from the query's inner execution
     * @param  ParameterizedQueryInterface $publisher Event originator
     * @param  array $params Parameters used in query
     * @return ParameterizedQueryEventInterface
     */
    public function __construct(
        $topic,
        $content,
        ParameterizedQueryInterface $publisher,
        array $params
    );
}
